import axios from 'axios';
import { LoginCredentials, LoginResponse, Exam, ApiResponse } from '../types';

const API_BASE_URL = 'http://localhost:3000';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor to include auth token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('access_token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Add response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('access_token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  },
);

export const authService = {
  login: async (credentials: LoginCredentials): Promise<LoginResponse> => {
    try {
      // Use fetch instead of axios to avoid CORS preflight issues
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Login response:', data);

      // Backend returns { data: { user, accessToken } }, extract and transform
      const { user, accessToken } = data.data;

      // Store the token and user
      localStorage.setItem('access_token', accessToken);
      localStorage.setItem('user', JSON.stringify(user));

      return {
        access_token: accessToken, // Convert accessToken → access_token
        user: user
      };
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  },

  logout: () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('user');
  },

  getCurrentUser: () => {
    const userStr = localStorage.getItem('user');
    return userStr ? JSON.parse(userStr) : null;
  },
};

export const examService = {
	getAllExams: async (params?: { search?: string; skip?: number; take?: number }): Promise<ApiResponse<Exam[]>> => {
		try {
			const token = localStorage.getItem("access_token");

			// Ensure search parameter is always sent (backend validation requires it)
			const queryParams = {
				search: params?.search || "",  // Send empty string if no search
				...(params?.skip && { skip: params.skip }),
				...(params?.take && { take: params.take })
			};

			const queryString = new URLSearchParams(queryParams).toString();

			const response = await fetch(`${API_BASE_URL}/exams?${queryString}`, {
				method: "GET",
				headers: {
					Authorization: `Bearer ${token}`,
					"Content-Type": "application/json",
				},
			});

			if (!response.ok) {
				const errorData = await response.json().catch(() => ({}));
				console.error('Get exams error:', errorData);
				throw new Error(`HTTP error! status: ${response.status}, message: ${errorData.error?.message || 'Unknown error'}`);
			}

			return await response.json();
		} catch (error) {
			console.error("Get exams error:", error);
			throw error;
		}
	},

  getExamById: async (id: number): Promise<Exam> => {
    const response = await api.get(`/exams/${id}`);
    return response.data;
  },

  pauseExam: async (id: number): Promise<Exam> => {
    const response = await api.patch(`/exams/${id}/pause`);
    return response.data;
  },

  unpauseExam: async (id: number): Promise<Exam> => {
    const response = await api.post(`/exams/${id}/unpause`);
    return response.data;
  },
};

export default api;
